import React, { useState } from "react";

function Login(props) {
  const usernameRegex = /^[a-zA-Z]+$/;
  const [usernameErrorMsg, setusernameErrorMsg] = useState("");
  const [localUser, setLocalUser] = useState({
    firstname: "",
    lastname: "",
    email: "",
    password: "",
  });
  const handleUsername = (e) => {
    if (!usernameRegex.test(e.target.value) && e.target.value) {
      setusernameErrorMsg("Please provide input in alphabets only");
    } else {
      setusernameErrorMsg("");
      console.log(e.target.id);
      console.log(e.target.value);

      if (e.target.id.localeCompare("firstname") == 0) {
        console.log(e.target.id);
        setLocalUser({ firstname: e.target.value });
      } else if (e.target.id.localeCompare("lastname") == 0) {
        console.log(e.target.id);
        setLocalUser({ lastname: e.target.value });
      }
      props.setUser(localUser);
      //props.setUsername(e.target.value);
    }
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    if (usernameErrorMsg.length > 0) {
      alert("Please resolve error");
    } else {
      props.setLoggedIn(true);
      alert("Successful submission with " + props.username);
    }
  };
  return (
    <div>
      <form>
        <h1>Login page</h1>
        <label>First Name: </label>
        <input type="text" id="firstname" onChange={handleUsername}></input>
        {usernameErrorMsg.length > 0 ? (
          <p style={{ color: "red" }}>usernameErrorMsg</p>
        ) : (
          ""
        )}
        <label>Last Name: </label>
        <input type="text" id="lastname" onChange={handleUsername}></input>
        {usernameErrorMsg.length > 0 ? (
          <p style={{ color: "red" }}>usernameErrorMsg</p>
        ) : (
          ""
        )}
        <button type="submit" onClick={handleSubmit}>
          Login
        </button>
      </form>
    </div>
  );
}

export default Login;
