import React, { useState } from "react";

function Login(props) {
  const usernameRegex = /^[a-zA-Z]+$/;
  const [usernameErrorMsg, setusernameErrorMsg] = useState("");
  const handleUsername = (e) => {
    if (!usernameRegex.test(e.target.value) && e.target.value) {
      setusernameErrorMsg("Please provide input in alphabets only");
    } else {
      setusernameErrorMsg("");
      props.setUsername(e.target.value);
    }
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    if (usernameErrorMsg.length > 0) {
      alert("Please resolve error");
    } else {
      props.setLoggedIn(true);
      alert("Successful submission with " + props.username);
    }
  };
  return (
    <div>
      <form>
        <h1>Login page</h1>
        <label>Username: </label>
        <input type="text" onChange={handleUsername}></input>
        {usernameErrorMsg.length > 0 ? (
          <p style={{ color: "red" }}>usernameErrorMsg</p>
        ) : (
          ""
        )}
        <button type="submit" onClick={handleSubmit}>
          Login
        </button>
      </form>
    </div>
  );
}

export default Login;
